CREATE TABLE Peliculas(
idPelicula INT IDENTITY NOT NULL,
TituloPelicula VARCHAR(150) NOT NULL,
Sinopsis NVARCHAR(MAX) NULL,
Director VARCHAR(120) NULL,
Género VARCHAR(50) NULL,
Calificacion DECIMAL(5,1)NULL,
Poster image NULL,

PRIMARY KEY (idPelicula)
)

